        &lt;result property=&quot;address&quot; column=&quot;address&quot;/&gt;
        &lt;result property=&quot;del&quot; column=&quot;del&quot;/&gt;
        &lt;collection property=&quot;wxUser&quot;  ofType=&quot;WxUser&quot;&gt;
